function revealer(){var e=document.getElementById("bpToggle");document.addEventListener("click",(function(e){if(e.target.classList.contains("toggle")){e.preventDefault();var t=document.querySelector(e.target.hash);if(t)t.classList.toggle("is-visible")}}),!1),e.addEventListener("click",(function(){var t=document.getElementById("bpToggle--text__collapsed").textContent;e.textContent.toLocaleLowerCase().includes(t.toLowerCase())?e.textContent=document.getElementById("bpToggle--text__expanded").textContent:e.textContent=t}))}revealer();